public class PositivetoNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int number=35;
		 if(number>0) {
			 System.out.println(number+ "Print number is Positive");}
			 else if(number<0)
			 {
				System.out.println(number+ "Print number is negative "); 
			 }
			 else
			 {
				 System.out.println("Print number is neither positive nor negative");
			 }
		 }
}

